int fa[N];

int Find(int x) {return fa[x] == x ? x : fa[x] = Find(fa[x]);}
long long Ans(int x)
{
	if (x & 1) return Ans(x - 1) * 26 % MOD;
	for (int i = 1; i <= x; i++) fa[i] = i;
	for (int i = 1; i <= x; i++)
		if (i & 1) fa[Find((x >> 1) + (i + 1 >> 1))] = Find(i);
		else fa[Find(i >> 1)] = Find(i);
	int c = 0;
	for (int i = 1; i <= x; i++)
		if (Find(i) == i) ++c;
	return Pow[c];
}
inline long long Query(int x)
{
	int block = x / SIZE;
	long long Ret = f[block];
	for (int i = block * SIZE + 1; i <= x; i++) Ret = (Ret + Ans(i)) % MOD;
	return Ret;
}

int main()
{
	Pow[0] = 1;
	for (int i = 1; i <= N; i++) Pow[i] = Pow[i - 1] * 26 % MOD;
	for (int i = 2; i <= 4000; i++) f[i] = (f[i] + f[i - 1]) % MOD;
	
	int q;
	cin >> q;
	while (q--)
	{
		int l, r;
		cin >> l >> r;
		cout << (Query(r) - Query(l - 1) + MOD) % MOD << endl;
	}
	
	return 0;
}

